$(document).ready(function(){
    AOS.init({disable:"mobile"})

    // Alert users to a non existing date when submitting
    $("#subscribe").submit(function(e) {
        e.preventDefault(); 
        var $emailBox = $("#email-box");
        if ($emailBox.val().length > 0) {
            alert("Thanks for joining our news letter!  You'll get an email on June 31st");
        }
        $emailBox.val("");
    })
    let answer = 342; // idk why i'm doing this
    let randomNumberForUser = Math.floor(Math.random() * 1000);
    console.log("Random number guess game puzzle thing");
    // idk how to format this, probably shouldn't do it in the first place
    let distance = randomNumberForUser > answer ? randomNumberForUser - answer : answer - randomNumberForUser;
    console.log(`You are: ${distance} away from the answer`) 
});